    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       
       
        //defining & initializing Arrays
        double gradeint[]=new double[14]; //array for gpa value for each grade
        gradeint[0]=0;
        gradeint[1]=4;
        gradeint[2]=4;
        gradeint[3]=3.7;
        gradeint[4]=3.3;
        gradeint[5]=3;
        gradeint[6]=2.7;
        gradeint[7]=2.3;
        gradeint[8]=2;
        gradeint[9]=1.7;
        gradeint[10]=1.3;
        gradeint[11]=1;
        gradeint[12]=0.7;
        gradeint[13]=0;
        
        String grade[]={" ","A+","A","A-","B+","B","B-","C+","C","C-","D+","D","D-","E"}; //array of grades
        String[][] ccode=new String[3][17]; //array for user input grades
        int cvalue[][]=new int[3][17];  //array for credit values of each course code
        double multiple[][]=new double[3][17]; //array for (credit*grade gpa value) of each coursecode
        
        
        //year1 sem1
        ccode[0][0]=c1.getSelectedItem().toString();
        ccode[0][1]=c2.getSelectedItem().toString();
        
        ccode[0][2]=c3.getSelectedItem().toString();
        ccode[0][3]=c4.getSelectedItem().toString();
        
        ccode[0][4]=c5.getSelectedItem().toString();
        ccode[0][5]=c6.getSelectedItem().toString();
        
        ccode[0][6]=c7.getSelectedItem().toString();
        ccode[0][7]=c8.getSelectedItem().toString();
        
        //year1 sem 2
        ccode[0][8]=cc1.getSelectedItem().toString();
        ccode[0][9]=cc2.getSelectedItem().toString();
        
        ccode[0][10]=cc3.getSelectedItem().toString();
        ccode[0][11]=cc4.getSelectedItem().toString();
        
        ccode[0][12]=cc5.getSelectedItem().toString();
        ccode[0][13]=cc6.getSelectedItem().toString();
        
        ccode[0][14]=cc7.getSelectedItem().toString();
        
        //year 2 sem1
        ccode[1][0]=ccc1.getSelectedItem().toString();
        ccode[1][1]=ccc2.getSelectedItem().toString();
        
        ccode[1][2]=ccc3.getSelectedItem().toString();
        ccode[1][3]=ccc4.getSelectedItem().toString();
        
        ccode[1][4]=ccc5.getSelectedItem().toString();
        ccode[1][5]=ccc6.getSelectedItem().toString();
        
        ccode[1][6]=ccc7.getSelectedItem().toString();
        
        ccode[1][7]=ccc8.getSelectedItem().toString();
        
        //year2 sem2
        ccode[1][8]=s1.getSelectedItem().toString();
        ccode[1][9]=s2.getSelectedItem().toString();
        
        ccode[1][10]=s3.getSelectedItem().toString();
        ccode[1][11]=s4.getSelectedItem().toString();
        
        ccode[1][12]=s5.getSelectedItem().toString();
        ccode[1][13]=s6.getSelectedItem().toString();
        
        ccode[1][14]=s7.getSelectedItem().toString();
        ccode[1][15]=s8.getSelectedItem().toString();
        
        ccode[1][16]=s9.getSelectedItem().toString();
        
        //year3 sem1
        ccode[2][0]=ss1.getSelectedItem().toString();
        ccode[2][1]=ss2.getSelectedItem().toString();
        
        ccode[2][2]=ss3.getSelectedItem().toString();
        ccode[2][3]=ss4.getSelectedItem().toString();
        
        ccode[2][4]=ss5.getSelectedItem().toString();
        ccode[2][5]=ss6.getSelectedItem().toString();
        
        //year3 sem2
        ccode[2][6]=sss1.getSelectedItem().toString();
        ccode[2][7]=sss2.getSelectedItem().toString();
        ccode[2][8]=sss3.getSelectedItem().toString();
        ccode[2][9]=sss4.getSelectedItem().toString();
        
        ccode[2][10]=sss5.getSelectedItem().toString();
        ccode[2][11]=sss6.getSelectedItem().toString();
  
        //get last sem?
        String sem=jc1.getSelectedItem().toString();
        int s,y;
        
        if(sem.equals("1st Year-1st Sem"))
        {
            s=8;
            y=1;
        }
        else if(sem.equals("1st Year-2nd Sem"))
        {
            y=1;
            s=15;
        }
        else if(sem.equals("2nd Year-1st Sem"))
        {
            y=2;
            s=8;
        }
        else if(sem.equals("2nd Year-2nd Sem"))
        {
            y=2;
            s=17;
        }
        else if(sem.equals("3rd Year-1st Sem"))
        {
            y=3;
            s=6;
        }
        else
        {
            y=3;
            s=12;
        }
        
       //Assigning credit value for each coursecode
        cvalue[0][0]=1;
        cvalue[0][1]=2;
        cvalue[0][2]=2;
        cvalue[0][3]=1;
        cvalue[0][4]=3;
        cvalue[0][5]=2;
        cvalue[0][6]=3;
        cvalue[0][7]=2;
        
        cvalue[0][8]=2;
        cvalue[0][9]=2;
        cvalue[0][10]=1;
        cvalue[0][11]=3;
        cvalue[0][12]=2;
        cvalue[0][13]=2;
        cvalue[0][14]=3;
        
        cvalue[1][0]=2;
        cvalue[1][1]=2;
        cvalue[1][2]=1;
        cvalue[1][3]=3;
        cvalue[1][4]=3;
        cvalue[1][5]=3;
        cvalue[1][6]=2;
        cvalue[1][7]=2;
        
        cvalue[1][8]=2;
        cvalue[1][9]=2;
        cvalue[1][10]=1;
        cvalue[1][11]=1;
        cvalue[1][12]=2;
        cvalue[1][13]=3;
        cvalue[1][14]=2;
        cvalue[1][15]=3;
        cvalue[1][16]=2;
        
        //add 3rd year credits to this later
        
        
        //calculating Multiples of each course code
        for(int row=0;row<y;row++)
        {
            for(int col=0;col<17;col++)
            {
              for(int check=0;check<14;check++)
              {
                 if(ccode[row][col]!=null && ccode[row][col].equals(grade[check]))
                 {
                    multiple[row][col]=((cvalue[row][col])*(gradeint[check]));
                    break;
                 }
              }
            }
        }
       
        //calculate gpa
        double total=0;
        for(int i=0;i<y;i++)
        {
            for(int j=0;j<17;j++)
            {
                total=total+multiple[i][j];
            }   
        }
        int ccount=0;
        for(int m=0;m<y;m++)
        {
            for(int n=0;n<17;n++)
            {
                if((ccode[m][n])!="")
                {
                  ccount=ccount+cvalue[m][n];
                }
            }
        }
        
        double gpa=(total/ccount);
        
        
        //display gpa
        jb.setText(Double.toString(gpa));
        
        //Determining Status & Display
        if(gpa>=3.7)
        {
            jb2.setVisible(true);
            jb2.setText("1st Class");
            jb2.setBackground(Color.green);
        }
        else if(gpa>=3.3)
        {
            jb2.setVisible(true);
            jb2.setText("2nd Upper");
            jb2.setBackground(Color.yellow);
        }
        else if(gpa>=3)
        {
            jb2.setVisible(true);
            jb2.setText("2nd Lower");
            jb2.setBackground(Color.orange);
        }
        else
        {
            jb2.setVisible(true);
            jb2.setText("General");
            jb2.setBackground(Color.red);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

